function BattleLevelStage_PreSettlePlot:__InitConfig()
	self.timeTotal = 3
	self.timeAcc = 0
	return true
end

function BattleLevelStage_PreSettlePlot:OnUpdate(_deltaTime)
	local _timeAcc = self.timeAcc
	_timeAcc = _timeAcc + _deltaTime
	self.timeAcc = _timeAcc
	if _timeAcc > self.timeTotal then
		self:OnStageEnd()
	end
end

