AssignPropBattleBuffEffect = class(BattleBuffEffect, 'AssignPropBattleBuffEffect');

function AssignPropBattleBuffEffect:ctor(_buffInstanceId)

    self.buffInstanceId = _buffInstanceId;
    self.castParam = nil;
    self.customParam = nil;
    self.nextTriggerTime = 0;
    self.triggerCount = 0;
