---@param _param table 自定义参数
---@return table 施法者参数
function ElementDamageBattleBuffEffect:GenerateCostCustomParam(_battleObjectBase,_buffInstanceId,_param)
    local _unit = _battleObjectBase:GetParentUnit()
    if _unit then
        local battleRoom = _unit:GetBattleRoom()
        return battleRoom.hurtManager:CreateSnapShot(_battleObjectBase:GetObjectId(), 2)
    end
    return nil;
end

return ElementDamageBattleBuffEffect;