LuaBehav_ConfigDataRequirer = {}
local mConfigPath = "Battle/Configs/"
local mRequireMap = {}

-- 
function LuaBehav_ConfigDataRequirer.GetConfig(_name)
	if not _name or 0 == #_name then return nil end

	local _config = mRequireMap[_name]
	if not _config then
		_config = require(mConfigPath .._name)
		if not _config then
			--LogTools.LogByLevel(LogLevel.log, "LuaBehav_ConfigDataRequirer", "can not get tree config","name",_name)
		end
		mRequireMap[_name] = _config
	end
	return _config
end



