-- 关卡计时器操作
function LuaBehav_Interfaces.LevelTimerOperation(_operationType)
	LuaBehav_DebugTool.PrintInterface("LuaBehav_Interfaces","LevelTimerOperation","_operationType",LuaBehav_DebugTool.GetLevelTimerOperationTypeDesc(_operationType))
	if LuaBehav_Define.LocalLuaDebug then return true end

	if LuaBehav_Define_LevelTimerOperationType.Start == _operationType then
		BATTLE_ROOM:OnBattleStart();
	elseif LuaBehav_Define_LevelTimerOperationType.Pause == _operationType then
		BATTLE_ROOM:OnBattlePause();
	elseif LuaBehav_Define_LevelTimerOperationType.Resume == _operationType then
		BATTLE_ROOM:OnBattleResume();
	elseif LuaBehav_Define_LevelTimerOperationType.Reset == _operationType then
		BATTLE_ROOM:OnBattleReset();
	end

	return true
end


-- 关卡投放所有玩家英雄
-- _id 为nil时表示所有玩家按照编队显示默认英雄，不为nil时表示所有玩家都为指定 id 的英雄
function LuaBehav_Interfaces.LevelDropAllPlayerHeroes(_id)
	LuaBehav_DebugTool.PrintInterface("LuaBehav_Interfaces","LevelDropPlayerHero","_id",_id)
	if LuaBehav_Define.LocalLuaDebug then return true end

	BATTLE_ROOM:CreateAllPlayerCharacter(_id);
	return true
end

