---@class RogueUnitTarget_NotRand : RogueUnitSkillTargetSelectorBase
RogueUnitTarget_NotRand = class(RogueUnitSkillTargetSelectorBase, "RogueUnitTarget_NotRand")

---@public
function RogueUnitTarget_NotRand:SelectTarget()
	local _unitList = self:__SelectCampType()
	local _objList = self:__SelectOnTeamState(_unitList)
	if not self.__numLimit then
		return _objList
	end
	local _leftObjNum = #_objList
	local _needNum = self.__maxNum

	while _leftObjNum > _needNum do
		table.remove(_objList, _leftObjNum)
		_leftObjNum = _leftObjNum - 1
	end
	
	return _objList
end

return RogueUnitTarget_NotRand