---@public
function RogueUnitTarget_SpecificElement:SelectTarget()
	local _unitList = self:__SelectCampType()
	local _objList = self:__SelectOnTeamState(_unitList)
	
	local _checkElementIdList = self.__xlsData.params
	local _ret = self.__retList
	TableUtil.ClearTable(_ret)
	local _curNum = 0
	---@type BattleObjCom_Config
	local _configCom
	---@type boolean
	local _get = false
	for i = 1, #_objList do
		_configCom = _objList[i]:GetComponent(BattleObjectComponentType.Config)
		if _configCom then
			_get = false
			for _, _elementId in pairs(_checkElementIdList) do
				if _configCom:GetElementId() == _elementId then
					_get = true
					break
				end
			end
			if _get then
				table.insert(_ret, _objList[i])
				_curNum = _curNum + 1
				if self.__numLimit and _curNum == self.__maxNum then
					return _ret
				end
			end
		end
	end
	
	return _ret
end

return RogueUnitTarget_SpecificElement