---事件回调，默认为空
---@public
function BattleLogicElementAttachEvent:FireEvent()
    if self.survival == 0 then
        local battleRoom = GetBattleRoom(self.battleId)
        ---@type BattleUnitBase
        local targetUnit = battleRoom.battleUnitManager:GetUnit(self.eventTriggerParam.targetNetId);
        ---行为树回调
        if targetUnit then
            ---@type BattleUnitCom_Camp
            local _campCom = targetUnit:GetComponent(BattleUnitComponentType.Camp)
            if _campCom then
                local _paramTable = {}
                _paramTable.elementId = self.eventCompareParam.elementId
                _paramTable.reactCamp = _campCom:GetFirstCamp()
                battleRoom.behaviorManager:PostTaskListenEvent(1, LuaBehav_Define_TaskListenEnum.EleAttach, _paramTable)
            end
        end
    end
end

