                ---@type BattleObjCom_Skill
                local skillComp = v:GetComponent(BattleObjectComponentType.Skill)
                if skillComp then
                    skillComp:OnElementEffect(self.eventTriggerParam.sourceNetId,self.eventTriggerParam.sourceObjectId,self.eventTriggerParam.targetNetId,self.eventTriggerParam.targetObjectId,self.eventCompareParam.elementId);
                end
            end
        end
        
