require "Lib/class"
require "Battle/Logic/Room/BattleLogicEvent/BattleLogicEventV2"

---@class BattleLogicElementReactEvent : BattleLogicEventV2
---@field eventType number 事件类型
---@field battleId number 战斗id
---@field eventCompareParam table 事件参数
---@field eventTriggerParam table 事件透传参数
---@field netId number 事件产出单位Id
---@field objectId number 事件产出物体Id
BattleLogicElementReactEvent = class(BattleLogicEventV2, 'BattleLogicElementReactEvent');

---@param _eventType number 事件类型
---@param _battleId number 战斗id
---@param _eventCompareParam table 事件参数
---@param _eventTriggerParam table 事件透传参数
---@param _netId number 事件产出单位Id
---@param _objectId number 事件产出物体Id
function BattleLogicElementReactEvent:ctor(_eventType, _eventCompareParam, _eventTriggerParam, _netId, _objectId, _battleId)
    self.battleId = _battleId
    self.eventType = _eventType;
    self.eventCompareParam = _eventCompareParam;
    self.eventTriggerParam = _eventTriggerParam;
    self.netId = _netId;
    self.objectId = _objectId;
    self.survival = 0;
end

---检测事件触发，默认没有比较参数
---@public
---@param _eventCompareParam table 比较参数
function BattleLogicElementReactEvent:CheckEventTrigger(_eventCompareParam)
    if _eventCompareParam.elementReactId == nil then
        return true
    end
    return self.eventCompareParam.elementReactId == _eventCompareParam.elementReactId;
end

---事件回调，默认为空
---@public
function BattleLogicElementReactEvent:FireEvent()
    if self.survival == 0 then
        local battleRoom = GetBattleRoom(self.battleId)
        ---@type BattleUnitBase
        local sourceUnit = battleRoom.battleUnitManager:GetUnit(self.eventTriggerParam.sourceNetId);
        ---@type BattleUnitBase
        local targetUnit = battleRoom.battleUnitManager:GetUnit(self.eventTriggerParam.targetNetId);
        -- 被动技能回调
        if sourceUnit then
            for i, v in pairs(sourceUnit:GetAllObjectDic()) do
                ---@type BattleObjCom_Skill
                local skillComp = v:GetComponent(BattleObjectComponentType.Skill)
                if skillComp then
                    skillComp:OnElementReact(self.eventTriggerParam.sourceNetId,self.eventTriggerParam.sourceObjectId,self.eventTriggerParam.targetNetId,self.eventTriggerParam.targetObjectId,self.eventCompareParam.elementReactId);
                end
            end
        end
        
        ---行为树回调
        if targetUnit then
            ---@type BattleUnitCom_Camp
            local _campCom = targetUnit:GetComponent(BattleUnitComponentType.Camp)
            if _campCom then
                local _paramTable = {}
                _paramTable.reactId = self.eventCompareParam.elementReactId
                _paramTable.reactCamp = _campCom:GetFirstCamp()
                battleRoom.behaviorManager:PostTaskListenEvent(1, LuaBehav_Define_TaskListenEnum.EleReact, _paramTable)
            end
        end

        ---数据收集
        battleRoom.dataCollectManager:OnElementReact(self.eventTriggerParam.sourceNetId, self.eventTriggerParam.targetNetId, self.eventCompareParam.elementReactId)
    end
end

---@public
---检测互斥事件
---@param _event BattleLogicEventV2 新事件
---@return boolean 是否互斥
function BattleLogicElementReactEvent:CheckMutexEvent(_event)
    return false;
end

---检测移除事件，默认事件会在trigger之后的下一帧被移除
---@public
function BattleLogicElementReactEvent:CheckRemoveEvent()
    self.survival = self.survival + 1;
    if self.survival > 1 then
        return true;
    end
    return false;
end

---生成比较参数
---@public
function BattleLogicElementReactEvent:GenerateCompareParam(_param);
    ---compareParam.elementReactId 反应id
    local compareParam = {};
    compareParam.elementReactId = tonumber(_param);
    return compareParam;
end

return BattleLogicElementReactEvent;