function BattleLogicPropChangeEvent:FireEvent()
    if self.survival == 0 then
        local battleRoom = GetBattleRoom(self.battleId)
        local _battleObject = battleRoom.battleObjectManager:GetObject(self.objectId)
        if _battleObject then
            ---@type BattleObjCom_Element_V2
            local _comElement = _battleObject:GetComponent(BattleObjectComponentType.Element)
            if _comElement then
                -- 元素监听属性改变
                _comElement:OnPropValueChange(self.eventTriggerParam.propId, self.eventTriggerParam.oldValue)
            end
        end
    end
end

