ElementStage_MaxReduceStage1 = class(ElementStageBase, 'ElementStage_MaxReduceStage1');

function ElementStage_MaxReduceStage1:Init(_index, _stageManager)
    self:__Init(_index, _stageManager)
    local _elementStackEffect = self.elementStackEffect
    self.reduceSpeed = _elementStackEffect.reduceSpeed[BATTLE_ELEMENT_CONST.ELEMENT_REDUCE_SPEED_M_INDEX] or 0
    --self.timeOut = _elementStackEffect.reduceSpeed[BATTLE_ELEMENT_CONST.ELEMENT_REDUCE_TIME_D_INDEX] or 0
    self.maxRatio = _elementStackEffect.reduceSpeed[BATTLE_ELEMENT_CONST.ELEMENT_MAX_ADD_RATIO_INDEX] or 0
end

function ElementStage_MaxReduceStage1:OnEnter()
    self:__OnEnter()
    if CheckLogLevel(LogLevel.log) and LogTools.ElementLog then
        local _targetObjectLog = "[" .. tostring(self.battleObjComElement.__object:GetDicId()) .. "]"
        local _unit = self.battleObjComElement.__object:GetParentUnit()
        if _unit:GetNetId() then
            _targetObjectLog = "[" .. self.battleRoom.battleUnitManager:GetBattleUnitTypeName(_unit:GetUnitType()) .. "][" .. _unit:GetNetId() .. "]" .. _targetObjectLog
        end
        LogTools.LogByLevel(LogLevel.log, "元素", "元素[" .. GetBattleElementNameById(self.elementId) .. "]", "目标" .. _targetObjectLog, "<color=#00FF00>进入创伤匀速衰减阶段</color>",
                "衰减速度", self.reduceSpeed,
                --"衰减时间", self.timeOut,
                "当前时间", TimeUtils.battleNow(self.battleId),
                "元素当前值", self.battleObjComElement:GetElementCurrentValue(self.elementId)
        )
    end
end

--- 获取衰减速度
