require "Lib/class"
require "Battle/Logic/Room/Component/InputDataSource"

---@class ClientInputDataSource : InputDataSource 客户端输入数据接收器
---@field clientUid number 启动logic_core的客户端id
ClientInputDataSource = class(InputDataSource,'ClientInputDataSource');

function ClientInputDataSource:ctor()

end

-- 初始化
---@public
---@param _clientUid number 启动logic_core的客户端id
function ClientInputDataSource:Init(_clientUid)
    self:__Init(_clientUid);
    self.clientUid = _clientUid;
end

-- 接收协议接口
---@public
---@param _uid number 发送协议的玩家id，如果是战斗房间消息该值为nil
---@param _id number 协议id
---@param _msgTable table 协议
function ClientInputDataSource:OnReceiveMessage(_uid, _id, _msgTable)
    local battleRoomMessage = BattleRoomMessage:New();
    local proto = PROTO_CODE_MAP[tostring(_id)];
    if proto ~= nil then
        if proto:GetType() == "ROOM" then
            battleRoomMessage:Init(_uid,_id,_msgTable);
            table.insert(self.roomMessages,battleRoomMessage);
        else
            if _uid == nil then
                _uid = self.clientUid;
            end
            battleRoomMessage:Init(_uid,_id,_msgTable);
            table.insert(self.playerCommands,battleRoomMessage);
        end
    end
end

-- 获得dict表
---@public
---@param _dictName string 表名
---@param _id number 表key
---@return table 表数据
function ClientInputDataSource:GetDict(_dictName,_id)
    return TableDic[_dictName][tostring(_id)]
end

return ClientInputDataSource;