require "Lib/class"
require "Battle/Logic/Skill/SkillBaseNode"

---@class SkillCoolDownNode : SkillBaseNode 技能coolDown节点
---@field nodeId number 节点id
---@field nextNodes SkillNodeExitData[] 子节点
---@field timeType number cd时间修改类别
---@field cdTime string cd时间
SkillCoolDownNode = class(SkillBaseNode, 'SkillCoolDownNode');

---@param _nodeId number 节点id
---@param _once boolean 只执行一次
---@param _nextNodes SkillNodeExitData[] 子节点
---@param _specialExit SkillNodeExitData[] 特殊出口
---@param _timeType number cd时间修改类别
---@param _cdTime string cd时间
function SkillCoolDownNode:ctor(_nodeId,_once, _nextNodes,_specialExit,_timeType,_cdTime)
    self.nodeId = _nodeId;
    self.once = _once;
    self.nextNodes = _nextNodes;
    self.specialExit = _specialExit;
    if _timeType ~= nil then
        self.timeType = _timeType
    else
        self.timeType = 0
    end
    self.cdTime = _cdTime;
end

---执行技能节点
---@param _actionStateManager ActionStateManage 行为管理器
---@param _dt number 更新时间
---@param _netId number 单位id
---@param _skillModel SkillModel 技能配置
---@param _currentSkillState SkillActionState 当前技能状态
---@return boolean,table 是否执行结束,执行行为输出数据
function SkillCoolDownNode:DoAction(_actionStateManager, _dt, _netId,_skillModel,_currentSkillState)
    ---@type BattleObjCom_Skill
    local componentSkill = _actionStateManager:GetUnit():GetCurrentHeroObject():GetComponent(BattleObjectComponentType.Skill);
    if componentSkill then
        local cdTime = _actionStateManager.battleRoom.inputDataSource:GetSkillFinalParam(self.cdTime,_skillModel.skillId,componentSkill:GetSkillLevel(_skillModel.skillId));
        if cdTime > 0 and self.timeType > 0 then
            local playerUnit = _actionStateManager:GetUnit()
            if playerUnit then
                ---@type BattleObjCom_Property
                local com_prop = playerUnit:GetCurrentHeroObject():GetComponent(BattleObjectComponentType.Property);
                if self.timeType == 1 then
                    cdTime = cdTime / (1 + tonumber(com_prop:GetPropertyByName(BattlePropertyEnum.Speed_Add_Atk)));
                elseif self.timeType == 2 then
                    cdTime = cdTime / (1 + tonumber(com_prop:GetPropertyByName(BattlePropertyEnum.Speed_Add_HodingAtk)));
                end
            else
                if CheckLogLevel(LogLevel.log) then
                    LogTools.LogByLevel(LogLevel.log, "playerUnit","playerUnit为空",_netId)
                end
                return nil
            end
        end
        componentSkill:SkillEnterCd(_skillModel.skillId,cdTime);
    end
    local doCurrentNode = {};
    doCurrentNode.nodeId = self.nodeId;
    return true,doCurrentNode;
end

---返回节点类型
---@public
---@return number 节点类型
function SkillCoolDownNode:GetType()
    return 6;
end

return SkillCoolDownNode;