require "Lib/class"
require "Battle/Logic/TalentSkill/TalentSkillResult/TalentSkillResultBase"

---@class TalentSkillAreaBuffResult : TalentSkillResultBase
TalentSkillAreaBuffResult = class(TalentSkillResultBase, 'TalentSkillAreaBuffResult');

function TalentSkillAreaBuffResult:ctor(_battleId)
    self.battleId = _battleId
    self.battleRoom = GetBattleRoom(self.battleId)
end

---触发被动技能
---@public
---@param _battleObjectBase BattleObjectBase
---@param _count number
---@param _talentSkillConfig TalentSkill
---@param _skillResult SkillResult
---@param _talentSkill BattlePlayerTalentSkill 被动技能
---@param _isReTrigger boolean 重新触发
function TalentSkillAreaBuffResult:Trigger(_battleObjectBase,_count,_talentSkillConfig,_skillResult,_talentSkill,_isReTrigger)
    ---@type BattleObjCom_Buff
    local buffComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Buff);
    if buffComp then
        if _skillResult.multTrigger == 0 and buffComp:CheckBuffExist(_skillResult.parm1) then
            return;
        end
        local upPercent = {};
        upPercent[0] = buffComp:GetTalentSkillUpPercent(_talentSkillConfig.sourceType);
        for i = 1, _count do
            buffComp:AddAreaBattleBuff(_skillResult.parm2, _skillResult.parm1,upPercent);
        end
        _talentSkill:AddTriggeredObject(_battleObjectBase:GetObjectId(),_count);
    end

end

---释放被动技能
---@public
---@param _skillResult SkillResult
---@param _targetObjectId number 伤害目标objectId
---@param _count number 触发次数
---@param _talentSkill BattlePlayerTalentSkill
function TalentSkillAreaBuffResult:Release(_skillResult, _targetObjectId,_count,_talentSkill)
    ---@type BattleObjectBase
    local targetObject = self.battleRoom.battleObjectManager:GetObject(_targetObjectId);
    if targetObject then
        ---@type BattleObjCom_Buff
        local buffComp = targetObject:GetComponent(BattleObjectComponentType.Buff);
        if buffComp then
            for i = 1, _count do
                buffComp:RemoveAreaBattleBuff(_skillResult.parm2, _skillResult.parm1);
            end
        end
    end
end


---被动技能触发类型
---@return number 被动技能触发类型
function TalentSkillAreaBuffResult:GetType()
    return 6;
end

return TalentSkillAreaBuffResult;