require "Lib/class"
require "Battle/Logic/TalentSkill/TalentSkillResult/TalentSkillResultBase"

---@class TalentSkillBarrageBuffResult : TalentSkillResultBase
TalentSkillBarrageBuffResult = class(TalentSkillResultBase, 'TalentSkillBarrageBuffResult');

function TalentSkillBarrageBuffResult:ctor(_battleId)
    self.battleId = _battleId
    self.battleRoom = GetBattleRoom(self.battleId)
end

---触发被动技能
---@public
---@param _battleObjectBase BattleObjectBase
---@param _count number
---@param _talentSkillConfig TalentSkill
---@param _skillResult SkillResult
---@param _talentSkill BattlePlayerTalentSkill 被动技能
---@param _isReTrigger boolean 重新触发
function TalentSkillBarrageBuffResult:Trigger(_battleObjectBase,_count,_talentSkillConfig,_skillResult,_talentSkill,_isReTrigger)

    ---@type BattleObjCom_Buff
    local buffComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Buff);
    if buffComp then
        if _skillResult.multTrigger == 0 then
            local buffs = buffComp:GetBarrageBattleBuff(_skillResult.parm2, _skillResult.parm3,0);
            if buffs and #buffs > 0 then
                return;
            end
        end
        for i = 1, _count do
            buffComp:AddBarrageBattleBuff(_skillResult.parm2, _skillResult.parm3,0,_skillResult.parm1);
        end
        _talentSkill:AddTriggeredObject(_battleObjectBase:GetObjectId(),_count);
    end

end

---释放被动技能
---@public
---@param _skillResult SkillResult
---@param _targetObjectId number 伤害目标objectId
---@param _count number 触发次数
---@param _talentSkill BattlePlayerTalentSkill
function TalentSkillBarrageBuffResult:Release(_skillResult, _targetObjectId,_count,_talentSkill)
    ---@type BattleObjectBase
    local targetObject = self.battleRoom.battleObjectManager:GetObject(_targetObjectId);
    if targetObject then
        ---@type BattleObjCom_Buff
        local buffComp = targetObject:GetComponent(BattleObjectComponentType.Buff);
        if buffComp then
            for i = 1, _count do
                buffComp:RemoveBarrageBattleBuff(_skillResult.parm2, _skillResult.parm1);
            end
        end
    end
end


---被动技能触发类型
---@return number 被动技能触发类型
function TalentSkillBarrageBuffResult:GetType()
    return 4;
end

return TalentSkillBarrageBuffResult;