require "Lib/class"
require "Battle/Logic/TalentSkill/TalentSkillResult/TalentSkillResultBase"

---@class TalentSKillBuffTimeResult : TalentSkillResultBase
TalentSKillBuffTimeResult = class(TalentSkillResultBase, 'TalentSKillBuffTimeResult');

function TalentSKillBuffTimeResult:ctor(_battleId)
    self.time = 0;
    self.battleId = _battleId
    self.battleRoom = GetBattleRoom(self.battleId)
end

---触发被动技能
---@public
---@param _battleObjectBase BattleObjectBase
---@param _count number
---@param _talentSkillConfig TalentSkill
---@param _skillResult SkillResult
---@param _talentSkill BattlePlayerTalentSkill 被动技能
---@param _isReTrigger boolean 重新触发
function TalentSKillBuffTimeResult:Trigger(_battleObjectBase,_count,_talentSkillConfig,_skillResult,_talentSkill,_isReTrigger)

    ---@type BattleObjCom_Buff
    local _buffCom = _battleObjectBase:GetComponent(BattleObjectComponentType.Buff)
    local time = _skillResult.parm2 * (1 + _buffCom:GetTalentSkillUpPercent(_talentSkillConfig.sourceType));
    self.time = time;

    _talentSkill:AddTriggeredObject(_battleObjectBase:GetObjectId(),_count);
    if not _skillResult.parm3 or _skillResult.parm3 == 1 then
        ---@type BattleObjCom_Buff
        local buffComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Buff);
        if buffComp then
            if _skillResult.multTrigger == 0 and buffComp:GetBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4) then
                return;
            end
            buffComp:AddBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4, time*_count);
        end
    else
        for i, object in pairs(_battleObjectBase:GetParentUnit():GetAllObjectDic()) do
            ---@type BattleObjCom_Buff
            local buffComp = object:GetComponent(BattleObjectComponentType.Buff);
            if buffComp then
                if _skillResult.multTrigger == 0 and buffComp:GetBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4) then
                    return;
                end
                buffComp:AddBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4, time*_count);
            end
        end
    end

end

---释放被动技能
---@public
---@param _skillResult SkillResult
---@param _targetObjectId number 伤害目标objectId
---@param _count number 触发次数
---@param _talentSkill BattlePlayerTalentSkill
function TalentSKillBuffTimeResult:Release(_skillResult, _targetObjectId,_count,_talentSkill)
    ---@type BattleObjectBase
    local targetObject = self.battleRoom.battleObjectManager:GetObject(_targetObjectId);
    if targetObject then
        if not _skillResult.parm3 or _skillResult.parm3 == 1 then
            ---@type BattleObjCom_Buff
            local buffComp = targetObject:GetComponent(BattleObjectComponentType.Buff);
            if buffComp then
                buffComp:AddBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4, self.time*-1*_count);
            end
        else
            for i, object in pairs(targetObject:GetParentUnit():GetAllObjectDic()) do
                ---@type BattleObjCom_Buff
                local buffComp = object:GetComponent(BattleObjectComponentType.Buff);
                if buffComp then
                    buffComp:AddBattleBuffExtraTime(_skillResult.parm1,_skillResult.parm4, self.time*-1*_count);
                end
            end
        end
    end
end


---被动技能触发类型
---@return number 被动技能触发类型
function TalentSKillBuffTimeResult:GetType()
    return 5;
end

