require "Lib/class"
require "Battle/Logic/TalentSkill/TalentSkillResult/TalentSkillResultBase"

---@class TalentSkillChangeSkillCoolDownResult : TalentSkillResultBase
TalentSkillChangeSkillCoolDownResult = class(TalentSkillResultBase, 'TalentSkillChangeSkillCoolDownResult');

function TalentSkillChangeSkillCoolDownResult:ctor(_battleId)
    self.battleId = _battleId
    self.battleRoom = GetBattleRoom(self.battleId)
    self.max = 0;
end

---触发被动技能
---@public
---@param _battleObjectBase BattleObjectBase
---@param _count number
---@param _talentSkillConfig TalentSkill
---@param _skillResult SkillResult
---@param _talentSkill BattlePlayerTalentSkill 被动技能
---@param _isReTrigger boolean 重新触发
function TalentSkillChangeSkillCoolDownResult:Trigger(_battleObjectBase,_count,_talentSkillConfig,_skillResult,_talentSkill,_isReTrigger)

    ---@type BattleObjCom_Buff
    local buffComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Buff);

    local skillId = _skillResult.parm1;
    local changeCd =  _skillResult.parm2 * (1 + buffComp:GetTalentSkillUpPercent(_talentSkillConfig.sourceType))*_count;
    ---@type BattleObjCom_Skill
    local skillComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Skill);
    if not skillComp then
        self.max = 0;
        return;
    end
    local coolDown = skillComp:GetSkillCoolDownTime(skillId);
    if coolDown <= 0 then
        self.max = 0;
        return;
    end

    if _skillResult.parm3 ~= 0 and self.max >= _skillResult.parm3 then
        self.max = 0;
        return;
    end

    skillComp:ChangeSkillCoolDown(skillId,changeCd);
    self.max = self.max + changeCd;

    _talentSkill:AddTriggeredObject(_battleObjectBase:GetObjectId(),_count);
end

---释放被动技能
---@public
---@param _skillResult SkillResult
---@param _targetObjectId number 伤害目标objectId
---@param _count number 触发次数
---@param _talentSkill BattlePlayerTalentSkill
function TalentSkillChangeSkillCoolDownResult:Release(_skillResult, _targetObjectId,_count,_talentSkill)
    self.max = 0;
end


---被动技能触发类型
---@return number 被动技能触发类型
function TalentSkillChangeSkillCoolDownResult:GetType()
    return 9;
end

return TalentSkillChangeSkillCoolDownResult;