---@class TalentSkillUpSkillLevelResult : TalentSkillResultBase
---@field battleRoom BattleRoom
TalentSkillUpSkillLevelResult = class(TalentSkillResultBase, 'TalentSkillUpSkillLevelResult');

function TalentSkillUpSkillLevelResult:ctor(_battleId)
    self.battleId = _battleId
    self.battleRoom = GetBattleRoom(self.battleId)
end

---触发被动技能
---@public
---@param _battleObjectBase BattleObjectBase
---@param _count number
---@param _talentSkillConfig TalentSkill
---@param _skillResult SkillResult
---@param _talentSkill BattlePlayerTalentSkill 被动技能
---@param _isReTrigger boolean 重新触发
function TalentSkillUpSkillLevelResult:Trigger(_battleObjectBase,_count,_talentSkillConfig,_skillResult,_talentSkill,_isReTrigger)
    ---@type BattleObjCom_Skill
    local skillComp = _battleObjectBase:GetComponent(BattleObjectComponentType.Skill);
    if skillComp then
        skillComp:UpSkillLevelByType(_skillResult.parm1, _skillResult.parm2*_count);
        _talentSkill:AddTriggeredObject(_battleObjectBase:GetObjectId(),_count);
    end
end

---释放被动技能
---@public
---@param _skillResult SkillResult
---@param _targetObjectId number 伤害目标objectId
---@param _count number 触发次数
---@param _talentSkill BattlePlayerTalentSkill
function TalentSkillUpSkillLevelResult:Release(_skillResult, _targetObjectId,_count,_talentSkill)
    ---@type BattleObjectBase
    local targetObject = self.battleRoom.battleObjectManager:GetObject(_targetObjectId);
    if targetObject then
        ---@type BattleObjCom_Skill
        local skillComp = targetObject:GetComponent(BattleObjectComponentType.Skill);
        if skillComp then
            skillComp:UpSkillLevelByType(_skillResult.parm1, _skillResult.parm2*-1*_count);
        end
    end
end


