    if _talentSkillTrigger.parm5 ~= 0 then
        if not self.targetDamageCount[_sourceObjectId] then
            self.targetDamageCount[_sourceObjectId] = 0;
        end
        self.targetDamageCount[_sourceObjectId] = self.targetDamageCount[_sourceObjectId] + 1;
        if self.targetDamageCount[_sourceObjectId] > self.damageCount then
            self.damageCount = self.targetDamageCount[_sourceObjectId];
