require "Lib/class"

---@class TalentSkillBeHealTrigger : table
TalentSkillBeHealTrigger = class(nil, 'TalentSkillBeHealTrigger');

function TalentSkillBeHealTrigger:ctor(_battleId)
    ---@type number
    self.battleId = _battleId
    self.lastTriggerTime = TimeUtils.battleNow(self.battleId);
    self.battleRoom = GetBattleRoom(self.battleId)
    self.count = 0;
    self.targetCount = {};
    self.targetNetId = 0;
    self.targetObjectId = 0;
end

---@public
---击中目标回调
---@param _talentSkillTrigger SkillTrigger
---@param _sourceNetId number 治疗来源netId
---@param _sourceObjectId number 治疗来源objectId
---@param _castSkillId number 治疗技能Id
function TalentSkillBeHealTrigger:OnBeHealed(_talentSkillTrigger, _sourceNetId,_sourceObjectId,_castSkillId)

    if TimeUtils.battleNow(self.battleId) - self.lastTriggerTime < _talentSkillTrigger.parm6 then
        return;
    end

    ---@type Skill
    local skillConfig = self.battleRoom.inputDataSource:GetDict("SKill",_castSkillId);
    if _talentSkillTrigger.parm8 and _talentSkillTrigger.parm8 ~= 0 and skillConfig then
        if skillConfig.skillType ~= _talentSkillTrigger.parm8 then
            return;
        end
    end

    if _talentSkillTrigger.parm5 ~= 0 then
        if not self.targetCount[_sourceObjectId] then
            self.targetCount[_sourceObjectId] = 0;
        end
        self.targetCount[_sourceObjectId] = self.targetCount[_sourceObjectId] + 1;
        if self.targetCount[_sourceObjectId] > self.count then
            self.count = self.targetCount[_sourceObjectId];
            self.targetNetId = _sourceNetId;
            self.targetObjectId = _sourceObjectId;
        end
    else
        self.count = self.count + 1;
    end

    self.lastTriggerTime = TimeUtils.battleNow(self.battleId);
end

---@public
---@param _battleObject BattleObjectBase 触发单位
---@param _talentSkillTrigger SkillTrigger 被动技能配置
---@param _talentSkill BattlePlayerTalentSkill 被动技能
function TalentSkillBeHealTrigger:Check(_battleObject, _talentSkillTrigger,_talentSkill)
    if _talentSkillTrigger.parm1 > self.count then
        return 0;
    end
    local count = math.floor(self.count / _talentSkillTrigger.parm1);
    if self.targetNetId ~= 0 and self.targetObjectId ~= 0 then
        for i, v in pairs(self.targetCount) do
            local tempCount = math.floor(v / _talentSkillTrigger.parm1);
            if tempCount > 0 then
                _talentSkill:AddTriggerObject(i,tempCount);
                self.targetCount[i] = nil;
            end
        end
    end
    self:Reset();
    return count;
end

---@public
function TalentSkillBeHealTrigger:Reset()
    self.count = 0;
end

---被动技能触发类型
---@return number 被动技能触发类型
function TalentSkillBeHealTrigger:GetType()
    return 131;
end

return TalentSkillBeHealTrigger;