-- 字符分割
---@type function
---@param _string string 字符串
---@param _separator string 分隔符
---@return string[] 分割后的数字
function split(_string, _separator)
    local nFindStartIndex = 1
    local nSplitIndex = 1
    local nSplitArray = {}
    while true do
        local nFindLastIndex = string.find(_string, _separator, nFindStartIndex)
        if not nFindLastIndex then
            nSplitArray[nSplitIndex] = string.sub(_string, nFindStartIndex, string.len(_string))
            break
        end
        nSplitArray[nSplitIndex] = string.sub(_string, nFindStartIndex, nFindLastIndex - 1)
        nFindStartIndex = nFindLastIndex + string.len(_separator)
        nSplitIndex = nSplitIndex + 1
    end
    return nSplitArray
end