    ["50"]=setmetatable({id=50,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=1,word="从这里可以进入【回忆模式】。",pic="help_memory_1"},mt),
    ["51"]=setmetatable({id=51,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=2,word="在回忆模式中，导师可以回顾之前的故事。点击可查看故事详情。",pic="help_memory_2"},mt),
    ["52"]=setmetatable({id=52,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=3,word="点击【作战准备】可以进入关卡重新体验。",pic="help_memory_3"},mt),
    ["53"]=setmetatable({id=53,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=4,word="可以在不同章节之间切换。",pic="help_memory_4"},mt),
    ["54"]=setmetatable({id=54,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=5,word="可以从这里查看此章节的收集奖励。",pic="help_memory_5"},mt),
    ["55"]=setmetatable({id=55,num=2003,openId=6461101,priority=3,classType=2,displayType=1,uiType=0,reward=1011,name="回忆模式",page=6,word="可以从这里切换不同的页签。",pic="help_memory_6"},mt),
    ["56"]=setmetatable({id=56,num=2004,openId=6102113,priority=4,classType=2,displayType=1,uiType=0,reward=1011,name="挑战模式",page=1,word="如果想要挑战更高难度的关卡，完成主线2-13后，可以从这里切换至挑战模式。",pic="help_hard_1"},mt),
    ["57"]=setmetatable({id=57,num=2004,openId=6102113,priority=4,classType=2,displayType=1,uiType=0,reward=1011,name="挑战模式",page=2,word="从这里可以体验【挑战关卡】。",pic="help_hard_2"},mt),
    ["58"]=setmetatable({id=58,num=2005,openId=6101110,priority=5,classType=2,displayType=1,uiType=0,reward=1011,name="资源获取",page=1,word="可以从这里获取一些需要的资源。可以根据需要进入不同的资源关卡。",pic="help_resource_1"},mt),
    ["59"]=setmetatable({id=59,num=2005,openId=6101110,priority=5,classType=2,displayType=1,uiType=0,reward=1011,name="资源获取",page=2,word="通过完成特定条件，可以开启外派作战功能。",pic="help_resource_7"},mt),
    ["60"]=setmetatable({id=60,num=2005,openId=6101110,priority=5,classType=2,displayType=1,uiType=0,reward=1011,name="资源获取",page=3,word="选择外派次数，消耗感知，等待一段时间后，即可领取外派奖励。\n通关时长将决定单次外派的耗时。\n还请导师多多尝试，努力缩短通关时长吧。\n注意：无法进行正在外派中的战斗。",pic="help_resource_8"},mt),
    ["61"]=setmetatable({id=61,num=2008,openId=620,priority=6,classType=2,displayType=1,uiType=0,reward=1011,name="脉流巡检",page=1,word="与至多3名玩家一同完成各区域的行政中心与巢会定期联名发布的清扫委托可获取大量资源，请在剩余时间前完成巡检任务。",pic="help_patrol_4"},mt),
    ["62"]=setmetatable({id=62,num=2008,openId=620,priority=6,classType=2,displayType=1,uiType=0,reward=1011,name="脉流巡检",page=2,word="在【巡检队伍】功能中可以邀请其他玩家一同参与脉流巡检任务。",pic="help_patrol_4"},mt),
    ["63"]=setmetatable({id=63,num=2008,openId=620,priority=6,classType=2,displayType=1,uiType=0,reward=1011,name="脉流巡检",page=3,word="每期巡检任务随时间开放巡检巡检任务关卡。",pic="help_patrol_6"},mt),
    ["64"]=setmetatable({id=64,num=2008,openId=620,priority=6,classType=2,displayType=1,uiType=0,reward=1011,name="脉流巡检",page=4,word="在脉流巡检玩法中可使用队友斯露德进行战斗，当前队伍中任一玩家都可带队通过巡检任务关卡。",pic="help_patrol_7"},mt),
    ["65"]=setmetatable({id=65,num=2010,openId=6103106,priority=7,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（一）",page=1,word="【交界虚空】共分为十层，曾被认为是TEO-11异空的一部分，后被证实为异空与现实的夹缝、缓冲地带，TEO-11对这里的干涉力度较弱，但仍有明显的【折叠】迹象。",pic="help_tower_1"},mt),
    ["66"]=setmetatable({id=66,num=2010,openId=6103106,priority=7,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（一）",page=2,word="交界虚空中，每一层由3个区域挑战组成，每个区域挑战战斗结束后选择“继续深入”可进入下一区域。连续深入的过程中不可以更换编队成员。",pic="help_tower_2"},mt),
    ["67"]=setmetatable({id=67,num=2010,openId=6103106,priority=7,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（一）",page=3,word="每个区域都可获取固定的协议酬劳奖励，3个区域全部通关后才可进入下一层。",pic="help_tower_3"},mt),
    ["68"]=setmetatable({id=68,num=2010,openId=6103106,priority=7,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（一）",page=4,word="交界虚空的每一层中，都存在着不同程度的【境界扭曲】，这种扭曲会对处于其中的角色、敌人造成不同形式、增益或减益的影响。\n请编队时一定认真考虑此效果再决定编队成员。",pic="help_tower_4"},mt),
    ["69"]=setmetatable({id=69,num=2010,openId=6103106,priority=7,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（一）",page=5,word="TEO-11每次折叠异空时，其异空内部的【深空律法】也会随之变化，在每层每个区域都会持续起效。",pic="help_tower_5"},mt),
    ["70"]=setmetatable({id=70,num=2011,openId=6500001,priority=8,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（二）",page=1,word="完成前置关卡后，会解锁难度较高的【虚空·解构】关卡,该关卡有特殊的奖励条件。\n在【虚空·解构】关卡中，即使导师挑战失败也会自动记录当前击败敌人数目作为排名依据，同时将失败关卡以及后续关卡以【最大通关耗时+1秒】的成绩记录。\n该关卡会在固定周期内重构，当虚空处于重构中，导师无法挑战【虚空·解构】。虚空每次重构后玩家可以重新获得关卡奖励。",pic="help_tower_6"},mt),
    ["71"]=setmetatable({id=71,num=2011,openId=6500001,priority=8,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（二）",page=2,word="完成【虚空·解构】关卡结算的玩家会被授予【虚空勋阶】，首次通关【虚空·解构】的时长会成为导师初始勋阶的依据，并且在首个完成关卡结算的周期内，导师可以重复挑战关卡以求获得更高的【虚空勋阶】\n每个周期中，导师会被随机分配至所处勋阶中的【阈限巡游队】，与同队的其他导师进行竞争。每次虚空重构后，导师会依据重构后所在的虚空勋阶获得对应的周期报酬。",pic="help_tower_7"},mt),
    ["72"]=setmetatable({id=72,num=2011,openId=6500001,priority=8,classType=2,displayType=1,uiType=0,reward=1011,name="交界虚空（二）",page=3,word="【阈限巡游队】会根据导师完成【虚空·解构】的最佳记录进行排名。巡游队中设有晋级区、保级区、降级区\n交界虚空重构后，处于晋级区的导师会晋升至下一【虚空勋阶】\n交界虚空重构后，处于保级区的导师会停留在当前【虚空勋阶】\n交界虚空重构后，处于降级区的导师会降级至上一【虚空勋阶】。",pic="help_tower_8"},mt),
    ["73"]=setmetatable({id=73,num=20020,openId=999,priority=9,classType=2,displayType=0,uiType=0,reward=1011,name="虚空火光",page=1,word="通过匹配或好友组队与其他导师一同战斗。\n将启用等效模拟，每位斯露德都将以60级的模拟属性加入战斗。",pic="help_duorenboss_1"},mt),
    ["74"]=setmetatable({id=74,num=20020,openId=999,priority=9,classType=2,displayType=0,uiType=0,reward=1011,name="虚空火光",page=2,word="每周2次奖励领取机会，周一4:00刷新奖励次数。",pic="help_duorenboss_2"},mt),
    ["75"]=setmetatable({id=75,num=20020,openId=999,priority=9,classType=2,displayType=0,uiType=0,reward=1011,name="虚空火光",page=3,word="没有奖励领取次数时，可以选择【协助作战】模式与好友一同挑战。",pic="help_duorenboss_3"},mt),
    ["76"]=setmetatable({id=76,num=2015,openId=6102112,priority=11,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（一）",page=1,word="每周开放2期，每期分匹配期、战斗期、结算期三个阶段。",pic="help_xiaozhanchang_1",vio=0},mt),
    ["77"]=setmetatable({id=77,num=2015,openId=6102112,priority=11,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（一）",page=2,word="匹配期为每周三和每周日的22:00开始，至次日的5:00结束，期间你将分配至与实力接近的导师为一组，共同清理空域，导师可查看本期自身及同小组中的导师的排名和积分。",pic="help_xiaozhanchang_2",vio=0},mt),
    ["78"]=setmetatable({id=78,num=2015,openId=6102112,priority=11,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（一）",page=3,word="战斗期为每周一和每周四的5:00开始，至每周三和周日的18:00结束，期间导师可率领斯露德剿灭异生体，获取积分，分数的高低将决定同组玩家之间的名次。",pic="help_xiaozhanchang_3",vio=0},mt),
    ["79"]=setmetatable({id=79,num=2015,openId=6102112,priority=11,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（一）",page=4,word="结算期为每周三和每周日的18:00开始，至当天22:00结束，期间将对导师率领斯露德剿灭异生体的英勇行为进行统计，并在下一期开启后发放奖励。",pic="help_xiaozhanchang_4",vio=0},mt),
    ["80"]=setmetatable({id=80,num=2016,openId=6102112,priority=12,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（二）",page=1,word="每期异空战线，根据导师所在小组分数高低进行排名，共有三个区间，分别为晋级区、保级区、降级区，结算时所在段位越高、排名区间越高，奖励越丰厚。",pic="help_xiaozhanchang_5",vio=0},mt),
    ["81"]=setmetatable({id=81,num=2016,openId=6102112,priority=12,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（二）",page=2,word="每期会异空战线随机产生一个乱流现象，不同的乱流现象对斯露德会产生不同的BUFF效果。",pic="help_xiaozhanchang_6",vio=0},mt),
    ["82"]=setmetatable({id=82,num=2016,openId=6102112,priority=12,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线（二）",page=3,word="异空战线中，每期开放一个地区，每个地区均有不同的地区特性，特性将对地区内的异生体产生增减益影响，使用合适的斯露德更容易打出较高分数，1个地区由3个空域组成，首个空域默认解锁，每通关一个空域，自动解锁下一区域，解锁后可重复挑战，计算地区总分数。",pic="help_xiaozhanchang_7",vio=0},mt),
    ["83"]=setmetatable({id=83,num=2017,openId=6102112,priority=13,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线-保护分",page=1,word="结算时，根据导师所在段位和排名将获得不同的降级保护分，保护分为100分达到最高上限，当导师位于降级区时，结算时，将优先扣除100点保护分，保留导师当期段位至下期异空战线。",pic="help_xiaozhanchang_8",vio=0},mt),
    ["84"]=setmetatable({id=84,num=2018,openId=6102112,priority=14,classType=2,displayType=1,uiType=0,reward=1011,name="异空战线-波动指数",page=1,word="波动指数影响段位内异生体的强度，在同一周期中波动指数不会改变。",pic="help_xiaozhanchang_9",vio=0},mt),
    ["85"]=setmetatable({id=85,num=2020,openId=6104112,priority=15,classType=2,displayType=1,uiType=0,reward=1011,name="裂隙封锁",page=1,word="【裂隙封锁】是一个以挑战强敌，获取更高积分为目标的玩法，在【裂隙封锁】中每周会有数种强敌供导师挑战，挑战强敌需要消耗挑战次数进行作战，挑战次数每日04:00刷新。",pic="help_dazhanchang_1"},mt),
    ["86"]=setmetatable({id=86,num=2020,openId=6104112,priority=15,classType=2,displayType=1,uiType=0,reward=1011,name="裂隙封锁",page=2,word="导师会根据等级自动分配等级组，不同的等级组的出现的强敌种类不同，关卡难度上限也不同。关卡难度越高，可以获得的积分也越高，请导师小心应对。",pic="help_dazhanchang_2"},mt),
    ["87"]=setmetatable({id=87,num=2020,openId=6104112,priority=15,classType=2,displayType=1,uiType=0,reward=1011,name="裂隙封锁",page=3,word="在裂隙封锁中不论是成功挑战强敌还是作战失败都会记录该场战斗的积分，导师可以自行选择保存或是放弃该次结果，只有选择保存时才会消耗挑战次数。",pic="help_dazhanchang_3"},mt),
    ["88"]=setmetatable({id=88,num=2020,openId=6104112,priority=15,classType=2,displayType=1,uiType=0,reward=1011,name="裂隙封锁",page=4,word="在【裂隙封锁】中出战的斯露德及武装会被锁定在对应的强敌中直至周期完结，在每种强敌中可出战的斯露德及武装有数量上限，请导师注意阵容规划。",pic="help_dazhanchang_4"},mt),
    ["89"]=setmetatable({id=89,num=2020,openId=6104112,priority=15,classType=2,displayType=1,uiType=0,reward=1011,name="裂隙封锁",page=5,word="周期结束时根据导师的总积分排名发放相应的排名奖励，奖励会在下个周期进入裂隙封锁时自动发放。",pic="help_dazhanchang_5"},mt),
    ["90"]=setmetatable({id=90,num=2021,openId=635,priority=16,classType=2,displayType=1,uiType=0,reward=1011,name="联行",page=1,word="联行玩法有三大阶段：组队阶段、竞速阶段、休整阶段。\n在组队阶段中导师可以选择自行创建队伍或是申请加入其他导师的队伍，与最多19名其他导师组成小队挑战联行关卡。",pic="help_guildwar_1"},mt),
    ["91"]=setmetatable({id=91,num=2021,openId=635,priority=16,classType=2,displayType=1,uiType=0,reward=1011,name="联行",page=2,word="在组队阶段结束后会开始队伍合并，此时无法加入、退出队伍。在该时段中同意自动分配的导师以及同意合并的队伍会被整合。\n若是队伍间合并，将会保留人数较多的队伍并且向被合并队伍的队长返还建队消耗\n若导师在组队阶段结束时没有队伍，则无法参与竞速阶段",pic="help_guildwar_2"},mt),
    ["92"]=setmetatable({id=92,num=2021,openId=635,priority=16,classType=2,displayType=1,uiType=0,reward=1011,name="联行",page=3,word="在竞速阶段中，每位导师需要指定一位斯露德作为“同行者”，挑战关卡时只能借用同行者来组成队伍进行挑战。一位导师的同行者每人每日只可借用一次\n若进入竞速阶段时队伍人数不满20人，则会自动补足NPC供导师组队",pic="help_guildwar_3"},mt),
    ["93"]=setmetatable({id=93,num=2021,openId=635,priority=16,classType=2,displayType=1,uiType=0,reward=1011,name="联行",page=4,word="竞速阶段中，队伍内任意导师完成联行关卡的三星条件后， 其他导师均可领取三星奖励并解锁后续关卡进度。",pic="help_guildwar_4"},mt),
    ["94"]=setmetatable({id=94,num=2021,openId=635,priority=16,classType=2,displayType=1,uiType=0,reward=1011,name="联行",page=5,word="竞速阶段结束后会进入休整阶段，此时会根据竞速阶中队伍的关卡进度以及星星获取数量进行排名并发送奖励",pic="help_guildwar_5"},mt),
    ["95"]=setmetatable({id=95,num=3001,openId=6101101,priority=1,classType=3,displayType=1,uiType=0,reward=1011,name="详情",page=1,word="从这里可以查看斯露德的【等级】【共鸣峰值】【职业】【属性】【符灵】【武装】等基础情况。",pic="help_detail_1"},mt),
    ["96"]=setmetatable({id=96,num=3001,openId=6101101,priority=1,classType=3,displayType=1,uiType=0,reward=1011,name="详情",page=2,word="【共鸣峰值】展示了斯露德当前的作战能力。",pic="help_detail_2"},mt),
    ["97"]=setmetatable({id=97,num=3001,openId=6101101,priority=1,classType=3,displayType=1,uiType=0,reward=1011,name="详情",page=3,word="【职业】展示了斯露德的作战能力方向。点击可以展开职业详情。",pic="help_detail_3"},mt),
