    ["1005032"]=setmetatable({id=1005032,startId=100503200,storyNote="声音的主人自称自己是奥黛。",title="声音",chapter=5,level=3},mt),
    ["1005041"]=setmetatable({id=1005041,startId=100504100,storyNote="三人来到奥黛面前，奥黛拉着格薇德林诉说自己的辛苦并且引诱着对方接受“蛇”的力量，甚至还想要把导师和法尼娅吞噬。",title="奥黛",chapter=5,level=4},mt),
    ["1005042"]=setmetatable({id=1005042,startId=100504200,storyNote="留下来的导师冷静地与奥黛周旋，探查出二人的关系与奥黛的软肋，最后找到机会离开。",title="奥黛",chapter=5,level=4},mt),
    ["1005051"]=setmetatable({id=1005051,startId=100505100,storyNote="格薇德林想要跟“蛇”决一死战，导师让法尼娅阻止。",title="心曲支点",chapter=5,level=5},mt),
    ["1005052"]=setmetatable({id=1005052,startId=100505200,storyNote="望见实力如此悬殊的“蛇”，导师不知道该如何下令让同伴们去战斗，没想到少女们却是一致的坚定。",title="心曲支点",chapter=5,level=5},mt),
    ["1005071"]=setmetatable({id=1005071,startId=100507100,storyNote="瓦哈纳众人援助格薇德林，奥黛发怒要杀死众人。",title="无关紧要",chapter=5,level=7},mt),
    ["1005081"]=setmetatable({id=1005081,startId=100508100,storyNote="在导师的帮助下，格薇德林终于将奥黛的身影从“蛇”中剥离，做出了最后的决定。",title="“蛇”",chapter=5,level=8},mt),
    ["1005091"]=setmetatable({id=1005091,startId=100509100,storyNote="格薇德林与奥黛之间对峙。",title="断绝",chapter=5,level=9},mt),
    ["1005111"]=setmetatable({id=1005111,startId=100511100,storyNote="通过观察战场，导师发现格薇德林也可以使用“蛇”的力量，并传达了自己的计划。为了打破僵局，格薇德林决定使用自己最痛恨的力量。",title="命运",chapter=5,level=11},mt),
    ["1005121"]=setmetatable({id=1005121,startId=100512100,storyNote="计划奏效，被打扰的“蛇”愤怒地召来了另外一个巨型异生体“鲸”来与瓦哈纳作战。",title="鲸落",chapter=5,level=12},mt),
    ["1005122"]=setmetatable({id=1005122,startId=100512200,storyNote="奥黛与格薇德林最后的告别，并且相信已经有人能代替自己站在格薇德林的身边。",title="鲸落",chapter=5,level=12},mt),
    ["1005131"]=setmetatable({id=1005131,startId=100513100,storyNote="格薇德林向导师致谢，并且告知了对方坎拉玛的信息。",title="凛冽的风",chapter=5,level=13},mt),
    ["1006001"]=setmetatable({id=1006001,startId=100600100,storyNote="来自“燧明”的斯露德小队——南方制造前往坎拉玛调查有关“涅石”的案件。",title="东方",chapter=6,level=0},mt),
    ["1006011"]=setmetatable({id=1006011,startId=100601100,storyNote="接到REVO采集任务的导师与法尼娅乔装成商人，前往坎拉玛城中调查。",title="涅石",chapter=6,level=1},mt),
    ["1006012"]=setmetatable({id=1006012,startId=100601200,storyNote="导师与法尼娅试图与城主交易“涅石”，却被代理人无名拒绝，此时，名为艾力西尔的男人终止了这场交易。由此，导师与法尼娅已经被多方监视，这其中也包括了南方制造……",title="涅石",chapter=6,level=1},mt),
    ["1006021"]=setmetatable({id=1006021,startId=100602100,storyNote="两支小队来到坎玛的消息惊动了幕后黑手艾力西尔，一个阴谋开始在他心中酝酿。",title="裂隙",chapter=6,level=2},mt),
    ["1006022"]=setmetatable({id=1006022,startId=100602200,storyNote="调查途中的导师与法尼娅被诡异的机器所吸引，却不知这是一个隐秘的陷阱……这个陷阱同时引来了南方制造的误会。",title="裂隙",chapter=6,level=2},mt),
    ["1006031"]=setmetatable({id=1006031,startId=100603100,storyNote="受伤昏迷的导师侥幸醒来，于此同时，一直怀疑导师的南方制造众人陷入了争论……",title="入夜",chapter=6,level=3},mt),
    ["1006032"]=setmetatable({id=1006032,startId=100603200,storyNote="遭到偷袭的南方制造三人重整旗鼓，决定按照计划继续调查。",title="入夜",chapter=6,level=3},mt),
    ["1006041"]=setmetatable({id=1006041,startId=100604100,storyNote="在确定南方制造与瓦哈纳的阵营一致后，导师开启了下一步计划。",title="利刃",chapter=6,level=4},mt),
    ["1006042"]=setmetatable({id=1006042,startId=100604200,storyNote="在事发地进行调查的顾途发现了“证据”的端倪，但她的队友司空却并不理解，南方制造的分歧与危机即将降临……",title="利刃",chapter=6,level=4},mt),
    ["1006051"]=setmetatable({id=1006051,startId=100605100,storyNote="因伤势而陷入昏迷的归陌做了一个很长的梦，关于她的过去，关于她的顾途……",title="理由",chapter=6,level=5},mt),
    ["1006061"]=setmetatable({id=1006061,startId=100606100,storyNote="导师与法尼娅通过原住民得知了这座城市不为人知的往事，与此同时，寻找队友的归陌却发现了受伤的顾途和司空。",title="无间",chapter=6,level=6},mt),
    ["1006071"]=setmetatable({id=1006071,startId=100607100,storyNote="重伤倒地的司空向归陌诉说了一些“不一样”的“真相”。",title="误解",chapter=6,level=7},mt),
    ["1006081"]=setmetatable({id=1006081,startId=100608100,storyNote="一心认为导师与法尼娅是敌人的归陌向二人发动了攻击。",title="争辩",chapter=6,level=8},mt),
    ["1006091"]=setmetatable({id=1006091,startId=100609100,storyNote="更多的异生体来到战场，战况激烈，归陌与法尼娅在混战中自顾不暇。",title="坠落",chapter=6,level=9},mt),
    ["1006092"]=setmetatable({id=1006092,startId=100609200,storyNote="战斗结束，归陌离开战场，司空下落不明。而在城市的暗处，艾力西尔开始谋划夺取“伊格之泪”的阴谋。",title="坠落",chapter=6,level=9},mt),
    ["1006101"]=setmetatable({id=1006101,startId=100610100,storyNote="大战之后的导师与法尼娅在坎拉玛中潜行，一位不速之客——无名突然找到二人，并邀请二人参与城主的“宴会”。",title="赴宴",chapter=6,level=10},mt),
    ["1007001"]=setmetatable({id=1007001,startId=100700100,storyNote="大漠深处，刚刚完成任务的神秘人与黑市首领艾力西尔会面，然而她的身份竟是一位“故人”……",title="虚妄",chapter=7,level=0},mt),
    ["1007011"]=setmetatable({id=1007011,startId=100701100,storyNote="导师应无名与城主邀请参加“宴会”，而无名奇怪的“谜语”与举动，引起了导师的注意。",title="牢笼",chapter=7,level=1},mt),
    ["1007012"]=setmetatable({id=1007012,startId=100701200,storyNote="导师应无名与城主邀请参加“宴会”，而无名奇怪的“谜语”与举动，引起了导师的注意。",title="牢笼",chapter=7,level=1},mt),
    ["1007013"]=setmetatable({id=1007013,startId=100701300,storyNote="原来，宴会上诡异的一切都是城主与无名的“求救信号”，由于黑市的计划败露，城主的安全危在旦夕。",title="牢笼",chapter=7,level=1},mt),
    ["1007021"]=setmetatable({id=1007021,startId=100702100,storyNote="在恶战中侥幸存活的司空在大漠中游荡，此时的她回忆起了自己不堪的过往，却遭遇了艾力西尔的暗算。",title="往事",chapter=7,level=2},mt),
    ["1007022"]=setmetatable({id=1007022,startId=100702200,storyNote="无名将司空背叛的情报告知归陌，并邀请归陌合作，醒来的顾途也说明了真相，归陌无法接受这样的事实，她决定找到司空问清楚。",title="往事",chapter=7,level=2},mt),
    ["1007031"]=setmetatable({id=1007031,startId=100703100,storyNote="受伤的归陌被导师所救，归陌终于相信导师，南方制造与瓦哈纳冰释前嫌达成合作。城主向几人提供了黑市的秘密据点——曾是坎拉玛之战的战场，几人找到藏匿所有“涅石”的祭台，这里充满陷阱，也充斥着大量异生体。",title="契约",chapter=7,level=3},mt),
    ["1007032"]=setmetatable({id=1007032,startId=100703200,storyNote="对抗异生体的战斗仍在持续……",title="契约",chapter=7,level=3},mt),
    ["1007041"]=setmetatable({id=1007041,startId=100704100,storyNote="通过战斗，几人拿到了全部的“涅石”，城主将“伊格之泪”靠近“涅石”验证，却发现“涅石”为假，就在这时，艾力西尔出现，原来这些假“涅石”本身就是“诱饵”……",title="陷阱",chapter=7,level=4},mt),
    ["1007051"]=setmetatable({id=1007051,startId=100705100,storyNote="良心备受煎熬的司空决定一洗罪孽，想于茫茫大漠中自我了断……",title="生与死",chapter=7,level=5},mt),
    ["1007061"]=setmetatable({id=1007061,startId=100706100,storyNote="战况逐渐焦灼，城主却不愿撤离，似乎，他隐瞒了一些关于坎拉玛与黑市之间的秘密……",title="罪与罚",chapter=7,level=6},mt),
    ["1007071"]=setmetatable({id=1007071,startId=100707100,storyNote="伴随着城主的回忆，众人了解到坎拉玛被黑市所欺骗奴役的往事，自觉愧对人民的城主欣然执行必死任务。",title="阴谋",chapter=7,level=7},mt),
    ["1007072"]=setmetatable({id=1007072,startId=100707200,storyNote="虽然城主慷慨赴死，但这依然无法改变此刻的战局，艾力西尔陷入了更加疯狂的境地。",title="阴谋",chapter=7,level=7},mt),
    ["1007081"]=setmetatable({id=1007081,startId=100708100,storyNote="被城主炸伤的艾力西尔陷入疯狂，就在战线即将溃败之际，司空挽回了战局……",title="混沌",chapter=7,level=8},mt),
    ["1007082"]=setmetatable({id=1007082,startId=100708200,storyNote="眼看自己即将失败，艾力西尔用毒器暗算了司空。",title="混沌",chapter=7,level=8},mt),
    ["1007091"]=setmetatable({id=1007091,startId=100709100,storyNote="司空因毒陷入了疯狂，法尼娅与归陌等人不得不对战司空。",title="牺牲",chapter=7,level=9},mt),
    ["1007092"]=setmetatable({id=1007092,startId=100709200,storyNote="在战斗最后一刻，司空突然恢复理智，坦然接受归陌的攻击，坠入沙丘。",title="呼唤",chapter=7,level=2},mt),
    ["1007101"]=setmetatable({id=1007101,startId=100710100,storyNote="完成涅石样本采集任务的瓦哈纳暂别众人，踏上新的征程。",title="记述",chapter=7,level=10},mt),
    ["1008001"]=setmetatable({id=1008001,startId=100800100,storyNote="雷雨之夜，一群诡秘之人相聚于某个秘密神殿，而他们所酝酿的阴谋正指向了瓦哈纳小队……",title="阴霾",chapter=8,level=0},mt),
    ["1008011"]=setmetatable({id=1008011,startId=100801100,storyNote="从噩梦中惊醒的导师开始对REVO产生怀疑，而就在这时，导师与法尼娅接收到了一则未知的通讯信号，而通讯者正是法尼娅的故友——斯卡蒂。",title="故梦",chapter=8,level=1},mt),
    ["1008012"]=setmetatable({id=1008012,startId=100801200,storyNote="斯卡蒂的突然到访令法尼娅回忆起了往事，但在此刻，斯卡蒂已不复从前那样天真，她的真实目的竟也与REVO有关，此刻的她希望可以与法尼娅在“约定之地”见面。",title="故梦",chapter=8,level=1},mt),
    ["1008021"]=setmetatable({id=1008021,startId=100802100,storyNote="由于“不速之客”的来访，福金对信号进行了紧急切断，尼伯赞歌号也因此被REVO总部的巡航舰逼停。",title="暗潮",chapter=8,level=2},mt),
    ["1008022"]=setmetatable({id=1008022,startId=100802200,storyNote="REVO特派员金百莉来访，告知导师交接“伊格之泪”的计划有变，原因竟是因为有叛徒来到了这片地区，并携带了“危险物体”。心觉异样的导师决定用计先稳住金百莉……",title="暗潮",chapter=8,level=2},mt),
    ["1008031"]=setmetatable({id=1008031,startId=100803100,storyNote="法尼娅依照导师的暗示逃离尼伯赞歌号，在这短暂的时差之内，来自REVO“曙光”的审判以“抗拒命令”之罪将留在舰船上的瓦哈纳成员全部扣押……",title="外来者",chapter=8,level=3},mt),
    ["1008032"]=setmetatable({id=1008032,startId=100803200,storyNote="法尼娅来到与斯卡蒂的“约定之地”，斯卡蒂向其叙述了自己在REVO某实验室的过往，原来“裂隙仪”与“长子”与REVO内部联系颇深……为了证明事件的真实，斯卡蒂呼唤出了自己所救的“长子”琪琪……",title="外来者",chapter=8,level=3},mt),
    ["1008041"]=setmetatable({id=1008041,startId=100804100,storyNote="与此同时，审判咄咄逼人的审讯仍在继续，导师以自己的智慧与瓦哈纳的实力使得审判做出了让步——导师将独自接受审判的问询。",title="审判",chapter=8,level=4},mt),
    ["1008042"]=setmetatable({id=1008042,startId=100804200,storyNote="初见“长子”的法尼娅无法坦然接受眼前的敌人，她与斯卡蒂的矛盾一触即发。",title="审判",chapter=8,level=4},mt),
    ["1008052"]=setmetatable({id=1008052,startId=100805200,storyNote="导师与审判的对峙仍在进行，审判节节败退。",title="纠缠",chapter=8,level=5},mt),
    ["1008053"]=setmetatable({id=1008053,startId=100805300,storyNote="导师与审判的对峙仍在进行，审判节节败退。",title="纠缠",chapter=8,level=5},mt),
    ["1008061"]=setmetatable({id=1008061,startId=100806100,storyNote="正当导师与审判的“战斗”进入到白热化时，法尼娅开启了异空裂隙。原来法尼娅的战斗行为只是对于斯卡蒂的试炼，而在此刻，斯卡蒂通过了试炼，法尼娅选择站在斯卡蒂的身边。",title="抉择",chapter=8,level=6},mt),
    ["1008062"]=setmetatable({id=1008062,startId=100806200,storyNote="突发情况打乱了审判的挑拨之计，众人逃离了审判的控制，但所有人都没有想到，审判尚有一枚棋子，那便是被大家忽略的金百莉……",title="抉择",chapter=8,level=6},mt),
    ["1008071"]=setmetatable({id=1008071,startId=100807100,storyNote="金百莉根据审判的命令攻击了琪琪与斯卡蒂，当法尼娅赶往现场时，斯卡蒂早已失踪，而自己面前的，只有死死掐住金百莉脖子的琪琪……法尼娅陷入了两难之境。",title="棋子",chapter=8,level=7},mt),
    ["1008072"]=setmetatable({id=1008072,startId=100807200,storyNote="面对两难抉择，法尼娅陷入了纠结，在她的冲动即将淹没情感之际，她受到了“另一个轮回”导师的指引，从而打破了僵局。",title="暂无",chapter=8,level=7},mt),
    ["1008081"]=setmetatable({id=1008081,startId=100808100,storyNote="发狂的琪琪开始操纵异生体攻击温德兰德，面对这一人为惨剧，导师与审判展开了关于信仰与神明的辩论……",title="异变",chapter=8,level=8},mt),
    ["1008082"]=setmetatable({id=1008082,startId=100808200,storyNote="为了挽救温德兰德，导师制定了对抗“长子”入侵的战术……",title="异变",chapter=8,level=8},mt),
    ["1008092"]=setmetatable({id=1008092,startId=100809200,storyNote="“长子”按计划送回了异空，哈纳的成员也有不同程度的受伤，面对受伤的队友和生死未卜的斯卡蒂，法尼娅无法接受现实，她冲向高空，无助地宣泄着自己的悲愤……",title="寻觅",chapter=8,level=9},mt),
    ["1008101"]=setmetatable({id=1008101,startId=100810100,storyNote="温德兰德事件逐渐平息，“曙光”成员真理开始执行保护证人金百莉的任务，然而审判的突然到访却引起了她的警觉……长路漫漫，有关于天空的真相究竟在何方呢？",title="花海",chapter=8,level=10},mt),
    ["1100011"]=setmetatable({id=1100011,startId=110001100,storyNote="黑色的敌人出现在天空中，“我”作为“导师”带领民众撤离。千钧一发之际，“我”护住即将遭到异生体攻击的小女孩。",title="序章上篇",chapter=0,level=1},mt),
    ["1100012"]=setmetatable({id=1100012,startId=110001200,storyNote="为了保护小女孩，导师被异生体击中，陷入昏迷。",title="序章上篇",chapter=0,level=1},mt),
    ["1100021"]=setmetatable({id=1100021,startId=110002100,storyNote="被称为“长子”的敌人击溃了前线斯露德的防线，前线斯露德格薇德林请求支援。导师身边的斯露德前去支援，留导师一人撤离群众。",title="序章下篇",chapter=0,level=1},mt),
    ["1100022"]=setmetatable({id=1100022,startId=110002200,storyNote="前线战况惨烈，导师与斯露德们失去了联系。",title="序章下篇",chapter=0,level=1},mt),
    ["1100023"]=setmetatable({id=1100023,startId=110002300,storyNote="强大的敌人使少女们陨落了……陷入绝望的导师被神秘人召唤至面前。神秘人向导师承诺，帮助导师改变这样的命运。",title="序章下篇",chapter=0,level=1},mt),
    ["1100024"]=setmetatable({id=1100024,startId=110002400,storyNote="看着斯露德们安然无恙，导师确信世界如神秘人所说的那样“改变”了。",title="序章下篇",chapter=0,level=2},mt),
    ["1101011"]=setmetatable({id=1101011,startId=110101100,storyNote="导师回忆起往事……故事开始于温德兰德斯露德学校。导师和法尼娅来到了这所学校讲课，问题学生奈纳从窗户飞进教室扰乱了秩序，在导师控场下讲座才顺利进行下去。",title="开端",chapter=1,level=1},mt),
    ["1101012"]=setmetatable({id=1101012,startId=110101200,storyNote="法尼娅演示了实战后，学生们积极提问。回答完学生们的问题后，法尼娅发现这些学生更向往安稳的生活，而不是夺回天空……她沉默了。",title="开端",chapter=1,level=1},mt),
    ["1101021"]=setmetatable({id=1101021,startId=110102100,storyNote="课后，法尼娅提醒导师，瓦哈纳小队来到温德兰德真正目的是寻找总部发现的大型异生体。在外调查的维拉联系上导师，她没发现大型异生体出没。为了任务顺利进行，导师提议去找一个当地向导。",title="掩盖",chapter=1,level=2},mt),
    ["1101031"]=setmetatable({id=1101031,startId=110103100,storyNote="导师和维拉在向导家门前会合，奈纳也出现在这里。法尼娅向奈纳询问向导——梅丽尔的事情，奈纳却不愿意配合。找不到向导的瓦哈纳小队只能按照常规方式搜索大型异生体。",title="巧合",chapter=1,level=3},mt),
    ["1101032"]=setmetatable({id=1101032,startId=110103200,storyNote="奈纳原来是梅丽尔的女儿，她也在调查大型异生体。不知道是什么原因，奈纳想要比瓦哈纳先找到大型异生体，她出门时遇到了夏洛，两人结伴寻找。",title="巧合",chapter=1,level=3},mt),
    ["1101041"]=setmetatable({id=1101041,startId=110104100,storyNote="奈纳回忆起自己的母亲梅丽尔被误解。温德兰德的居民不相信有大型异生体存在，他们认为是梅丽尔指挥失误导致驻地斯露德小队出事。为了证明母亲的清白，奈纳要找出大型异生体。",title="过往",chapter=1,level=4},mt),
    ["1101051"]=setmetatable({id=1101051,startId=110105100,storyNote="瓦哈纳小队发现了游荡在“帆”外的夏洛。从夏洛口中得知奈纳失踪了。寻找奈纳途中，大家发现了大型异生体。",title="“虎鲸”",chapter=1,level=5},mt),
    ["1101052"]=setmetatable({id=1101052,startId=110105200,storyNote="奈纳找到了大型异生体——“虎鲸”。初次直面“虎鲸”的奈纳，因恐惧失去了作战能力。",title="“虎鲸”",chapter=1,level=5},mt),
    ["1101061"]=setmetatable({id=1101061,startId=110106100,storyNote="导师联系上了绝望中的奈纳，指挥她迫降在地面上。",title="迫降",chapter=1,level=6},mt),
    ["1101071"]=setmetatable({id=1101071,startId=110107100,storyNote="导师上前查看奈纳的状况，她拒绝了导师的好意，跑进森林中。此时天上的异生体向导师聚拢过来……",title="抗拒",chapter=1,level=7},mt),
    ["1101081"]=setmetatable({id=1101081,startId=110108100,storyNote="夏洛找到奈纳后，发现她躲藏在角落里，不肯出来。情急之下，维拉强行把奈纳转移到了安全的尼伯赞歌号上。",title="封闭",chapter=1,level=8},mt),
    ["1101082"]=setmetatable({id=1101082,startId=110108200,storyNote="回到尼伯赞歌号上后，众人批评奈纳危险的做事方法。奈纳想起母亲一个人寻找大型异生体，被温德兰德的居民认为是疯子。奈纳以为瓦哈纳的人也这么看待自己，跑出了船舱。",title="封闭",chapter=1,level=8},mt),
    ["1101091"]=setmetatable({id=1101091,startId=110109100,storyNote="夏洛向导师讲述了奈纳的遭遇……奈纳的母亲在流言蜚语中一心调查“虎鲸”，最终在调查“虎鲸”的途中去世了。众人知道这些后，同情起了奈纳。导师找到奈纳，帮她重拾对抗“虎鲸”的勇气。",title="计划",chapter=1,level=9},mt),
    ["1101101"]=setmetatable({id=1101101,startId=110110100,storyNote="导师决定去做诱饵，引出“虎鲸“。奈纳逐渐向大家敞开心扉，担心起大家的人身安全。",title="关心",chapter=1,level=10},mt),
    ["1101111"]=setmetatable({id=1101111,startId=110111100,storyNote="导师成功破坏了“虎鲸”能喷出云雾的器官。“虎鲸”暴露在斯露德们面前。夏洛被小型异生体包围，为了救夏洛，奈纳放弃了亲手了结“虎鲸”的机会。",title="了断",chapter=1,level=11},mt),
    ["1101112"]=setmetatable({id=1101112,startId=110111200,storyNote="法尼娅消灭了“虎鲸”。奈纳去母亲的墓碑前，告诉她“虎鲸”被消灭的消息。导师让维拉把夏洛和奈纳带过来，邀请她们加入瓦哈纳。奈纳对突如其来的邀请和后续安排有些抵触。",title="了断",chapter=1,level=11},mt),
    ["1101113"]=setmetatable({id=1101113,startId=110111300,storyNote="瓦哈纳小队离开了温德兰德去往波伽兰德。",title="告别",chapter=1,level=11},mt),
    ["3001011"]=setmetatable({id=3001011,startId=300101100,storyNote="瓦哈纳小队接到赶往越野社进行支援的任务，听说是一位越野社的成员在某次作战后陷入昏迷不醒的状态。",title="疑思",chapter=1,level=1},mt),
    ["3001012"]=setmetatable({id=3001012,startId=300101200,storyNote="在越野社遇到了阐释工坊·进化组的三叶，她告知众人加利福也许是受到精神冲击而昏迷。但很快三叶也因为照顾加利福多日留下一个用于监测斯露德生命体征的小仪器“诺姆”，和“委托了朋友来帮忙”的讯息之后就累的睡着了。",title="疑思",chapter=1,level=2},mt),
    ["3001021"]=setmetatable({id=3001021,startId=300102100,storyNote="导师和瓦哈纳小队的众人暂时也没有好办法，只能一边帮助清理附近的异生体，一点等待三叶所说的“朋友”。战斗中，一个陌生人突然出现，自说自话的要跟众人“比赛”。",title="痛梦",chapter=2,level=1},mt),
    ["3001022"]=setmetatable({id=3001022,startId=300102200,storyNote="战斗结束，刚刚的陌生人戏谑着捉弄大家，随之来了几个人将她阻止。原来她们就是三叶请来帮忙的“朋友”，来自“痛梦逻辑”。为首二人解释了何为梦境，并准备带大家一起进入加利福的梦，寻找她昏迷的原因。",title="痛梦",chapter=2,level=2},mt),
    ["3001031"]=setmetatable({id=3001031,startId=300103100,storyNote="突然出现的神秘人打断了众人的入梦行动。在与麦当然的交谈中，她似乎暗示着梦境深处有未知的危险。但麦当然还是说服了她，让入梦行动得以继续进行。",title="密谈",chapter=3,level=0},mt),
    ["3001041"]=setmetatable({id=3001041,startId=300104100,storyNote="韦伯再次准备好仪器，留下痛梦逻辑的成员安迪和泡芙场外支援之后，众人在麦当然的指引下正式进入梦境。",title="入梦",chapter=4,level=1},mt),
    ["3001042"]=setmetatable({id=3001042,startId=300104200,storyNote="正式进入梦境，在扫清眼前的障碍之后，众人准备开始寻找线索。",title="入梦",chapter=4,level=2},mt),
    ["3001051"]=setmetatable({id=3001051,startId=300105100,storyNote="飞行中，麦当然看到一个奇怪的小家伙。她很好奇的追了上去。",title="锚点",chapter=5,level=1},mt),
    ["3001052"]=setmetatable({id=3001052,startId=300105200,storyNote="梦中的场景发生了改变。麦当然告诉大家如果不想在梦境中迷失，需要找到自己心中的锚点。",title="锚点",chapter=5,level=2},mt),
    ["3001061"]=setmetatable({id=3001061,startId=300106100,storyNote="走廊无穷无尽，每推开一扇门就会有新的危机出现。但麦当然发现，她对每个新场景越来越熟悉。",title="失控",chapter=8,level=1},mt),
    ["3001062"]=setmetatable({id=3001062,startId=300106200,storyNote="事情开始失控，这个梦境显然已经不是加利福的梦境了。情况并不明朗，麦当然和韦伯在此时发生争执。",title="失控",chapter=8,level=1},mt),
    ["3001071"]=setmetatable({id=3001071,startId=300107100,storyNote="韦伯向导师和瓦哈纳小队说明了麦当然的情况和自己的担忧，没想到众人都愿意先了解这个梦境，尽可能帮助麦当然。大家继续前行，想发现隐藏在门后的，关于麦当然的心结。",title="梦魇",chapter=9,level=1},mt),
    ["3001081"]=setmetatable({id=3001081,startId=300108100,storyNote="麦当然的回忆：曾经濒死被人救下，麦当然意外获得了入梦的能力。再想要感谢恩人并分享天赋的时候，麦当然这位学姐因为耗能严重，已经快要死去了。",title="如梦往事1",chapter=10,level=0},mt),
    ["3001091"]=setmetatable({id=3001091,startId=300109100,storyNote="麦当然的回忆：为了研究自己的天赋，麦当然结交了学校里最博学的韦伯。在韦伯的建议下，麦当然决定用自己的方式，陪伴学姐昆汀走完生命最后的旅程。",title="如梦往事2",chapter=11,level=0},mt),
    ["3001101"]=setmetatable({id=3001101,startId=300110100,storyNote="麦当然的回忆：麦当然带着昆汀不停的冒险，韦伯研究从梦境中带出的讯息。她们一起度过的这一段快乐时间，是麦当然心中宝贵的回忆。",title="如梦往事3",chapter=12,level=0},mt),
    ["3001111"]=setmetatable({id=3001111,startId=300111100,storyNote="温德兰德的一切恍若好梦，惊起的白鸽与突兀的滴答声，麦当然再次回到了虚无的黑暗与梦境之中。",title="心结",chapter=13,level=1},mt),
    ["3001112"]=setmetatable({id=3001112,startId=300111200,storyNote="昆汀质问着麦当然的心结与痛苦，企图将她引进梦境的更深处。",title="蛊惑",chapter=13,level=1},mt),
    ["3001121"]=setmetatable({id=3001121,startId=300112100,storyNote="韦伯曾经在麦当然失控时进入过麦当然的梦，虽然那是很久以前的事情了。她对麦当然的心结有过诸多猜测，大家一起分析，也渐渐有了眉目。而此时，梦境突然变幻，一群异生体攻了过来。",title="观梦",chapter=14,level=1},mt),
    ["3001122"]=setmetatable({id=3001122,startId=300112200,storyNote="导师推断麦当然已经陷入自己的梦魇，潜意识在攻击这些可能把她叫醒的人。大家必须尽快行动去解救她。",title="观梦",chapter=14,level=2},mt),
    ["3001131"]=setmetatable({id=3001131,startId=300113100,storyNote="大家终于找到了麦当然，她仿佛失去了神志，一步步向昆汀走去……",title="坠落",chapter=16,level=1},mt),
    ["3001132"]=setmetatable({id=3001132,startId=300113200,storyNote="战斗结束，但麦当然还没有苏醒。在导师的启发下，韦伯终于想到了能唤回她神志的办法。那是她们之间的暗号，她知道。那也是她的锚点，她不知道。",title="怀表",chapter=16,level=2},mt),
    ["3001141"]=setmetatable({id=3001141,startId=300114100,storyNote="昆汀终于变回原来的样子了，她知道自己其实只是麦当然心中的映射。但即便如此，她仍然想要好好的跟二人道别。",title="梦终于梦",chapter=17,level=0},mt),
    ["3001151"]=setmetatable({id=3001151,startId=300115100,storyNote="梦境恢复秩序，行动也可以重回正轨。这一次行动可以顺利展开吗？导师回忆起种种迹象，担忧的用手指抵住眉心……",title="梦归于梦",chapter=18,level=0},mt),
    ["3002011"]=setmetatable({id=3002011,startId=300201100,storyNote="闪烁的小魂魄再次出现，引导着众人进入了一个氛围十分梦幻的空间。",title="最闪耀的星",chapter=1,level=1},mt),
    ["3002012"]=setmetatable({id=3002012,startId=300201200,storyNote="众人终于追赶到加利福的小魂魄，发现她正十分忧伤的发呆中。询问得知，她正在因为自己的舞台破碎而流浪在梦境中。为了解救加利福出梦境，大家决定为她完成梦想。",title="最闪耀的星",chapter=1,level=2},mt),
    ["3002021"]=setmetatable({id=3002021,startId=300202100,storyNote="众人明显感知到这个梦境和麦当然的梦境氛围完全不同，趋近于离谱的程度。韦伯表示梦境的事物都是趋向于个人认知，完全可能出现同一事物但所有人认知不同的情况。但若是所有人都达成了统一认知，就很有可能改变梦境的表现。",title="想入非非的梦",chapter=2,level=0},mt),
    ["3002031"]=setmetatable({id=3002031,startId=300203100,storyNote="加利福想要的舞台碎片竟然就是三叶贩卖的三叶虫……等等？三叶怎么也进来梦境了？还开起了商店？",title="开门大吉",chapter=3,level=2},mt),
    ["3002041"]=setmetatable({id=3002041,startId=300204100,storyNote="为了得到足够的金币给加利福换取三叶虫，大家在梦境中游荡，寻找到了许多散落的金币。",title="金币雨",chapter=4,level=1},mt),
    ["3002042"]=setmetatable({id=3002042,startId=300204200,storyNote="众人千辛万苦终于收集到了一定的数目，可以跟三叶兑换三叶虫。但收集的金币和三叶虫存量比起来简直是杯水车薪，所以导师决定要想别的办法……",title="金币雨",chapter=4,level=2},mt),
    ["3002052"]=setmetatable({id=3002052,startId=300205200,storyNote="经过阐述，大家分为两条线完成任务，一边是防止三叶会不断地补货，一边是去探索三叶的线索。",title="通关手册",chapter=5,level=2},mt),
    ["3002061"]=setmetatable({id=3002061,startId=300206100,storyNote="寻找原产地的三人偷偷摸摸跟踪三叶，发现她到了一片金币堆。",title="跟踪",chapter=6,level=2},mt),
    ["3002071"]=setmetatable({id=3002071,startId=300207100,storyNote="那个紧握在手上未能赠送出去的三叶虫，成为了推向三叶成为斯露德的手。",title="迟到",chapter=7,level=0},mt),
    ["3002081"]=setmetatable({id=3002081,startId=300208100,storyNote="奈纳和麦当然天马行空地思考找线索的方法，结果反而绊住了自己的步伐。",title="打劫！",chapter=8,level=1},mt),
    ["3002082"]=setmetatable({id=3002082,startId=300208200,storyNote="奈纳和麦当然两人“默契”合作，轻轻松松地就让三叶起了警觉。但她们还没来得及反应，竟然听到了一个熟悉的声音。",title="打劫！",chapter=8,level=2},mt),
    ["3002101"]=setmetatable({id=3002101,startId=300210100,storyNote="如果世事有不公，那就让我——三叶，来拨开这遮眼的云！",title="初心",chapter=10,level=0},mt),
    ["3002111"]=setmetatable({id=3002111,startId=300211100,storyNote="了解三叶过往的两队人马被三叶的过去所感动，决定老老实实完成她的梦想。",title="梦想",chapter=11,level=1},mt),
    ["3002122"]=setmetatable({id=3002122,startId=300212200,storyNote="这不过是个美梦，一个美丽的金色梦乡。",title="金色梦乡",chapter=12,level=2},mt),
    ["3002132"]=setmetatable({id=3002132,startId=300213200,storyNote="三叶虫终于贩卖完毕啦！三叶虫商店也要关门大吉，虽然遗憾，但三叶觉得做这么一场梦也是值得。更何况她决定——要加入大家的舞台建设中，这么有趣的事情，肯定不能放过！",title="关门大吉",chapter=13,level=2},mt),
    ["3002142"]=setmetatable({id=3002142,startId=300214200,storyNote="三叶与加利福的梦圆满结束，此时天边却裂开了一道裂隙，引导着众人离开梦境。",title="好梦初醒",chapter=15,level=2},mt),
    ["3002151"]=setmetatable({id=3002151,startId=300215100,storyNote="众人热热闹闹搭建舞台，虽然闹出了很多事情令人啼笑皆非。但属于加利福闪耀的舞台，终于搭建好了！",title="SHOWTIME",chapter=14,level=0},mt),
    ["3002161"]=setmetatable({id=3002161,startId=300216100,storyNote="从梦中惊醒的加利福从那道裂隙中回想起，此前她调查到有人利用裂隙仪打开了缝隙。更可怕的是，在本不该有人的裂隙外，闪过一个熟悉的人影……",title="那个身影",chapter=16,level=0},mt),
    ["4001011"]=setmetatable({id=4001011,startId=400101100,storyNote="什么是梦境，什么是异空。危机降临，与新伙伴一起登场的，除了埋藏在心底的沉疴，还有我们一直未曾关注的隐秘。当梦与真实的联系被揭开，我们可触及到的，是真相，亦或是深渊……",title="异梦显现",chapter=1,level=1},mt),
    ["4001012"]=setmetatable({id=4001012,startId=400101200,storyNote="什么是梦境，什么是异空。危机降临，与新伙伴一起登场的，除了埋藏在心底的沉疴，还有我们一直未曾关注的隐秘。当梦与真实的联系被揭开，我们可触及到的，是真相，亦或是深渊……",title="异梦显现",chapter=1,level=2},mt),
    ["4001021"]=setmetatable({id=4001021,startId=400102100,storyNote="麦当然和韦伯入梦后，驻守在外泡芙和安迪感受到了不安的气息……",title="不安渐生",chapter=2,level=0},mt),
    ["4001031"]=setmetatable({id=4001031,startId=400103100,storyNote="符灵的研发对于阐释工坊来说是很大进步，虽然……现在大家也只把符灵当做智能家居就是了……",title="Chapter3",chapter=3,level=0},mt),
